import React, { useState } from 'react';
import './Nav.css';
import { useNavigate } from "react-router-dom";

function Nav() {
  const [menuOpen, setMenuOpen] = useState(false); // State to manage menu visibility
  const navigate = useNavigate();

  const toggleMenu = () => {
    setMenuOpen(!menuOpen); // Toggle the menu state
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-logo">
          <img src="logo.png" alt="Logo" />
          <span className="logoname">Portfolio</span>
        </div>
        
        {/* Hamburger Icon */}
        <div className="hamburger" onClick={toggleMenu}>
          â˜°
        </div>

        {/* Navigation Menu */}
        <ul className={`nav-menu ${menuOpen ? "active" : ""}`}>
          <li className="nav-item" onClick={() => navigate("/")}>Home</li>
          <li className="nav-item" onClick={() => navigate("/portfolio")}>Portfolio</li>
          <li className="nav-item" onClick={() => navigate("/Resume")}>Resume</li>
          <li className="nav-item" onClick={() => navigate("/contact")}>Contact</li>
        </ul>
      </div>
    </nav>
  );
}

export default Nav;
